import styled from 'styled-components';

export const Container = styled.div`
  background: ${({ theme }) => theme.primary_light};
  border-radius: 8px;
  border: 2px solid ${({ theme }) => theme.primary_light};
  padding: 16px;
  width: 100%;

  input {
    & + input {
      margin-top: 8px;
    }
  }
`;
