import styled from 'styled-components';

import background from '../../assets/background.jpg';

export const Container = styled.div`
  height: 100vh;

  display: flex;
  align-items: stretch;
`;

export const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  width: 100%;
  max-width: 700px;

  img {
    width: 200px;
  }

  form {
    display: flex;
    flex-direction: column;

    width: 350px;
    text-align: center;

    h1 {
      margin-bottom: 16px;
    }

    input {
      background: ${({ theme }) => theme.primary_light};
      border-radius: 8px;
      border: 2px solid ${({ theme }) => theme.primary_light};
      padding: 16px;
      width: 100%;

      & + input {
        margin-top: 8px;
      }
    }

    button {
      background: ${({ theme }) => theme.button_color};
      height: 56px;
      border-radius: 8px;
      border: 0;
      padding: 0 16px;
      width: 100%;
      color: ${({ theme }) => theme.primary};
      font-weight: bold;
      font-size: 18px;
      margin-top: 16px;

      transition: all 0.3s;

      &:hover {
        transform: scale(1.02);
      }
    }

    a {
      color: ${({ theme }) => theme.button_color};
      display: block;
      margin-top: 24px;
      text-decoration: none;

      &:hover {
        transform: scale(1.02);
      }
    }
  }

  > a {
    color: ${({ theme }) => theme.button_color};
    display: block;
    margin-top: 24px;
    text-decoration: none;

    &:hover {
      transform: scale(1.02);
    }
  }
`;

export const Background = styled.div`
  flex: 1;
  background: url(${background}) no-repeat;
  background-size: cover;
  filter: contrast(0.1);
`;
